library(tidyverse)
library(rvest)
url &lt;- "https://rvest.tidyverse.org/articles/starwars.html"
read_html(url) |&gt; 
  html_elements("section") |&gt; 
  html_element("h2") |&gt; 
  html_text2()





[1] "The Phantom Menace"      "Attack of the Clones"   
[3] "Revenge of the Sith"     "A New Hope"             
[5] "The Empire Strikes Back" "Return of the Jedi"     
[7] "The Force Awakens"      





section &lt;- read_html(url) |&gt; 
  html_elements("section")
tibble(
  title = section |&gt; 
    html_element("h2") |&gt; 
    html_text2(),
  released = section |&gt; 
    html_element("p") |&gt; 
    html_text2() |&gt; 
    str_remove("Released: ") |&gt; 
    parse_date(),
  director = section |&gt; 
    html_element(".director") |&gt; 
    html_text2(),
  intro = section |&gt; 
    html_element(".crawl") |&gt; 
    html_text2()
)





